/************************************************************************
 L.Marker
 - GSI.CenterCrossMarker（中心マーク）
 ************************************************************************/
GSI.CenterCrossMarker = L.Marker.extend({
  _initIcon: function () {
    var options = this.options,
      map = this._map,
      animation = (map.options.zoomAnimation && map.options.markerZoomAnimation),
      classToAdd = animation ? 'leaflet-zoom-animated' : 'leaflet-zoom-hide';

    var icon = options.icon.createIcon(this._icon),
      addIcon = false;

    // if we're not reusing the icon, remove the old one and init new one
    if (icon !== this._icon) {
      if (this._icon) {
        this._removeIcon();
      }
      addIcon = true;

      if (options.title) {
        icon.title = options.title;
      }

      if (options.alt) {
        icon.alt = options.alt;
      }
    }

    L.DomUtil.addClass(icon, classToAdd);

    if (options.keyboard) {
      icon.tabIndex = '0';
    }

    this._icon = icon;

    this._initInteraction();

    if (options.riseOnHover) {
      L.DomEvent
        .on(icon, 'mouseover', this._bringToFront, this)
        .on(icon, 'mouseout', this._resetZIndex, this);
    }

    var newShadow = options.icon.createShadow(this._shadow),
      addShadow = false;

    if (newShadow !== this._shadow) {
      this._removeShadow();
      addShadow = true;
    }

    if (newShadow) {
      L.DomUtil.addClass(newShadow, classToAdd);
    }
    this._shadow = newShadow;

    if (options.opacity < 1) {
      this._updateOpacity();
    }

    var panes = this._map._panes;

    if (addIcon) {
      panes.gsiObjectsPane.appendChild(this._icon);
    }

    if (newShadow && addShadow) {
      panes.objectsPane.appendChild(this._shadow);
    }
  },
  _removeIcon: function () {
    if (this.options.riseOnHover) {
      L.DomEvent
        .off(this._icon, 'mouseover', this._bringToFront)
        .off(this._icon, 'mouseout', this._resetZIndex);
    }

    this._map._panes.gsiObjectsPane.removeChild(this._icon);

    this._icon = null;
  },
  _removeShadow: function () {
    if (this._shadow) {
      this._map._panes.objectsPane.removeChild(this._shadow);
    }
    this._shadow = null;
  }
});

/************************************************************************
 L.Popup
 ・_updateLayout上書き
 ・tableのwidth指定時修正
 ************************************************************************/
L.Popup.prototype._updateLayout = function () {
  var container = this._contentNode,
    style = container.style;

  var table = $(container).find("table");
  var tableWidth = null;

  if (table.length > 0) {
    tableWidth = table.attr('width');
    if (!tableWidth) {
      tableWidth = table[0].style.width;
    }

  }

  style.width = '';
  if (!tableWidth) style.whiteSpace = 'nowrap';

  var width = container.offsetWidth;
  width = Math.min(width, this.options.maxWidth);
  width = Math.max(width, this.options.minWidth);

  style.width = (width + 1) + 'px';
  style.whiteSpace = '';

  style.height = '';

  var height = container.offsetHeight,
    maxHeight = this.options.maxHeight,
    scrolledClass = 'leaflet-popup-scrolled';

  if (maxHeight && height > maxHeight) {
    style.height = maxHeight + 'px';
    L.DomUtil.addClass(container, scrolledClass);
  } else {
    L.DomUtil.removeClass(container, scrolledClass);
  }

  this._containerWidth = this._container.offsetWidth;
};

