        L.drawLocal.draw.handlers.polygon.tooltip.cont = '次の位置を選択';
        L.drawLocal.draw.handlers.polygon.tooltip.end = GSI.TEXT.LINEEDITTIP + '次の位置を選択(最終点を2回クリックして終了)';

