// クッキーが利用可能かチェック
GSI.SaveStateManager.checkEnableCookie = function () {
  var result = true;

  if (navigator.cookieEnabled) {
    document.cookie = 'enabled_cookie=1';
    if (document.cookie) {
      if (document.cookie.length > 0) {
        if (document.cookie.indexOf('enabled_cookie') > -1) {
          result = true;
        }
      }
    }
  } else {
    result = false;
  }

  return result;
};

